const messages = [
  'Building smarter homes with open-source hardware. #SmartHome #IoT',
  'Presence detection that respects your privacy. No cloud required. #HomeAutomation',
  'Hardware + software designed to work together. #HardwareOS #Tech',
  'Automating the mundane so you can focus on what matters. #SmartHome',
  'Open source, locally controlled, privacy-first smart home. #HomeAssistant #IoT',
  'From ESP32 sensors to full home automation. #Makers #DIY',
  'Your data stays in your home. #Privacy #LocalControl',
  'Crafting the future of home tech. #OpticWorks #Innovation'
];

const now = new Date();
const idx = (now.getHours() + Math.floor((now - new Date(now.getFullYear(), 0, 0)) / 86400000)) % messages.length;

return [{ json: { text: messages[idx] } }];
